mapper.select:
SELECT e.nombre AS 'empresa.nombre', e.nombre_comercial AS 'empresa.nombre_comercial', e.logo AS 'empresa.logo', e.nombre_representante AS 'empresa.nombre_representante', e.email AS 'empresa.email', e.telefono AS 'empresa.telefono', e.pagina_web AS 'empresa.pagina_web', e.rfc AS 'empresa.rfc', e.estatus_id AS 'empresa.estatus_id',
d.calle AS 'domicilio.calle', d.numero AS 'domicilio.numero', d.colonia AS 'domicilio.colonia', d.codigo_postal AS 'domicilio.codigo_postal', d.id_ciudad AS 'domicilio.id_ciudad'
FROM empresa e INNER JOIN domicilio d ON e.direccion_id = d.id WHERE e.id = #{id}

mapper.insert:
{CALL registrarEmpresa( #{empresa.nombre, mode=IN, jdbcType=VARCHAR}, #{empresa.nombre_comercial, mode=IN, jdbcType=VARCHAR}, #{empresa.nombre_representante, mode=IN, jdbcType=VARCHAR}, #{empresa.email, mode=IN, jdbcType=VARCHAR}, #{empresa.telefono, mode=IN, jdbcType=VARCHAR}, #{empresa.pagina_web, mode=IN, jdbcType=VARCHAR}, #{empresa.rfc, mode=IN, jdbcType=VARCHAR},
#{domicilio.calle, mode=IN, jdbcType=VARCHAR}, #{domicilio.numero, mode=IN, jdbcType=INTEGER}, #{domicilio.colonia, mode=IN, jdbcType=VARCHAR}, #{domicilio.codigo_postal, mode=IN, jdbcType=INTEGER}, #{domicilio.id_ciudad, mode=IN, jdbcType=INTEGER},
#{filasAfectadas, mode=INOUT, jdbcType=INTEGER}, #{error, mode=INOUT, jdbcType=VARCHAR} )}

mapper.update:
{CALL editarEmpresa( #{empresa.id, mode=IN, jdbcType=INTEGER}, #{empresa.nombre, mode=IN, jdbcType=VARCHAR}, #{empresa.nombre_comercial, mode=IN, jdbcType=VARCHAR}, #{empresa.nombre_representante, mode=IN, jdbcType=VARCHAR}, #{empresa.email, mode=IN, jdbcType=VARCHAR}, #{empresa.telefono, mode=IN, jdbcType=VARCHAR}, #{empresa.logo, mode=IN, jdbcType=BLOB}, #{empresa.pagina_web, mode=IN, jdbcType=VARCHAR},
#{domicilio.calle, mode=IN, jdbcType=VARCHAR}, #{domicilio.numero, mode=IN, jdbcType=INTEGER}, #{domicilio.colonia, mode=IN, jdbcType=VARCHAR}, #{domicilio.codigo_postal, mode=IN, jdbcType=INTEGER}, #{domicilio.id_ciudad, mode=IN, jdbcType=INTEGER},
#{filasAfectadas, mode=INOUT, jdbcType=INTEGER}, #{error, mode=INOUT, jdbcType=VARCHAR} )}

UPDATE empresa
   SET logo = #{logo} 
   WHERE id = #{id}

mapper.delete:
DELETE FROM empresa
WHERE id = #{id}
AND NOT EXISTS (
  SELECT 1
  FROM sucursal
  WHERE sucursal.empresa_id = #{id}
)
