mapper.select:
SELECT s.nombre AS 'sucursal.nombre', s.telefono AS 'sucursal.telefono', s.latitud AS 'sucursal.latitud', s.longitud AS 'sucursal.longitud', s.nombre_encargado AS 'sucursal.nombre_encargado',
d.calle AS 'domicilio.calle', d.numero AS 'domicilio.numero', d.colonia AS 'domicilio.colonia', d.codigo_postal AS 'domicilio.codigo_postal', d.id_ciudad AS 'domicilio.id_ciudad'
FROM sucursal s INNER JOIN domicilio d ON s.direccion_id = d.id WHERE s.id = #{id}

mapper.insert:
{CALL registrarSucursal( #{sucursal.nombre, mode=IN, jdbcType=VARCHAR}, #{sucursal.telefono, mode=IN, jdbcType=VARCHAR}, #{sucursal.latitud, mode=IN, jdbcType=DECIMAL}, #{sucursal.longitud, mode=IN, jdbcType=DECIMAL}, #{sucursal.nombre_encargado, mode=IN, jdbcType=VARCHAR}, #{sucursal.empresa_id, mode=IN, jdbcType=INTEGER},
#{domicilio.calle, mode=IN, jdbcType=VARCHAR}, #{domicilio.numero, mode=IN, jdbcType=INTEGER}, #{domicilio.colonia, mode=IN, jdbcType=VARCHAR}, #{domicilio.codigo_postal, mode=IN, jdbcType=INTEGER}, #{domicilio.id_ciudad, mode=IN, jdbcType=INTEGER},
#{filasAfectadas, mode=INOUT, jdbcType=INTEGER}, #{error, mode=INOUT, jdbcType=VARCHAR} )}
